        public static string getCubeName(SessionInfo si, int cubeId)
        {
            string cubeName = "";
            try
            {
                var cubes_DT = new DataTable("Cubes");
                var dbConnApp = BRApi.Database.CreateApplicationDbConnInfo(si);
                using (var connection = new SqlConnection(dbConnApp.ConnectionString))
                {
                    var sql_gbl_get_datasets = new GBL_UI_Assembly.SQL_GBL_Get_DataSets(si, connection);
                    // Create a new DataTable
                    var sqlDataAdapter = new SqlDataAdapter();
                    // Define the select query and parameters
                    string selectQuery = @"
			        	SELECT Name
			       		FROM Cube
			       		WHERE CubeId = @OS_Cube_ID";
                    // Create an array of SqlParameter objects
                    var parameters = new SqlParameter[]
                    {
                        new SqlParameter("@OS_Cube_ID", SqlDbType.Int) { Value = cubeId }
                    };
                    sql_gbl_get_datasets.Fill_Get_GBL_DT(si, sqlDataAdapter, cubes_DT, selectQuery, parameters);

                }
                if (cubes_DT.Rows.Count > 0)
                {
                    cubeName = cubes_DT.Rows[0]["Name"].ToString();
                }

                return cubeName;

            }
            catch (Exception ex)
            {
                throw ErrorHandler.LogWrite(si, new XFException(si, ex));
            }
        }

        public static Dictionary<string, string> getParamsToAdd(DataTable headerItems)
        {
            Dictionary<string, string> paramVals = new Dictionary<string, string>();

            foreach (DataRow item in headerItems.Rows)
            {

                string baseSearch = "DDM_Menu_Hdr_";

                string optType = item[baseSearch + "Option_Type"].ToString();

                if (optType == "Filter")
                {
                    baseSearch += "Fltr";

                    foreach (string colSuffix in DDM_Header.dashboardTypeResolver.Keys)
                    {
                        if ((bool)item[baseSearch + "_" + colSuffix] && colSuffix != "Txt")
                        {
                            string dimType = item[baseSearch + "_Dim_Type"].ToString();

                            // Dimension
                            if (paramVals.ContainsKey($"IV_DDM_App_{dimType}_Dim_Name"))
                            {
                                paramVals[$"IV_DDM_App_{dimType}_Dim_Name"] = item[baseSearch + "_Dim_Name"].ToString();
                            }
                            else
                            {
                                paramVals.Add($"IV_DDM_App_{dimType}_Dim_Name", item[baseSearch + "_Dim_Name"].ToString());
                            }

                            // Member filter
                            if (paramVals.ContainsKey($"IV_DDM_App_{dimType}_MFB"))
                            {
                                paramVals[$"IV_DDM_App_{dimType}_MFB"] = item[baseSearch + "_MFB"].ToString();
                            }
                            else
                            {
                                paramVals.Add($"IV_DDM_App_{dimType}_MFB", item[baseSearch + "_MFB"].ToString());
                            }

                            // set the ML value here directly
                            if (paramVals.ContainsKey($"ML_DDM_App_{dimType}_Selection"))
                            {
                                paramVals[$"ML_DDM_App_{dimType}_Selection"] = item[baseSearch + "_Default"].ToString();
                            }
                            else
                            {
                                paramVals.Add($"ML_DDM_App_{dimType}_Selection", item[baseSearch + "_Default"].ToString());
                            }


                            //set default ML, this will default the ML to this everytime it is loaded
                            //paramVals.Add($"IV_DDM_App_{dimType}_ML_Default", item[baseSearch + "_Default"].ToString()); // default val for ML



                        }
                    }
                }
            }

            return paramVals;
        }

        public static int getCurrentProfileID(SessionInfo si, Guid profileKey)
        {
            var configProfileDT = new DataTable("configProfileDT");

            int profileID = -1;

            var dbConnApp = BRApi.Database.CreateApplicationDbConnInfo(si);
            using (var connection = new SqlConnection(dbConnApp.ConnectionString))
            {
                var sql_gbl_get_datasets = new GBL_UI_Assembly.SQL_GBL_Get_DataSets(si, connection);

                var sqlDataAdapter = new SqlDataAdapter();

                string selectQuery = @"
										Select DDM_Profile_ID
										From DDM_Config
										Where DDM_Profile_Name = @OS_ProfileKey";

                var parameters = new SqlParameter[] {
                    new SqlParameter("@OS_ProfileKey", SqlDbType.UniqueIdentifier) { Value = profileKey }
                };

                if (profileKey != null)
                {
                    sql_gbl_get_datasets.Fill_Get_GBL_DT(si, sqlDataAdapter, configProfileDT, selectQuery, parameters);
                }
            }

            if (configProfileDT.Rows.Count > 0)
            {
                profileID = Convert.ToInt32(configProfileDT.Rows[0]["DDM_Profile_ID"]);
            }

            return profileID;
        }

        public static DataTable getConfigMenuOptions(SessionInfo si, int profileID, int SelectedMenuOption)
        {

            var configMenuOptionsDT = new DataTable("configMenuOptionsDT");
            if (profileID != -1 && SelectedMenuOption != -1)
            {
                var dbConnApp1 = BRApi.Database.CreateApplicationDbConnInfo(si);
                using (var connection = new SqlConnection(dbConnApp1.ConnectionString))
                {
                    var sql_gbl_get_datasets = new GBL_UI_Assembly.SQL_GBL_Get_DataSets(si, connection);

                    var sqlDataAdapter = new SqlDataAdapter();

                    string selectQuery = @"
											Select *
											From DDM_Config_Menu
											Where DDM_Menu_ID = @Menu_Option
											And DDM_Profile_ID = @ProfileID";

                    var parameters = new SqlParameter[] {
                        new SqlParameter("@Menu_Option", SqlDbType.Int) { Value = SelectedMenuOption },
                        new SqlParameter("@ProfileID", SqlDbType.Int) { Value = profileID }
                    };

                    sql_gbl_get_datasets.Fill_Get_GBL_DT(si, sqlDataAdapter, configMenuOptionsDT, selectQuery, parameters);
                }
            }

            return configMenuOptionsDT;

        }

        public static int getSelectedMenuOption(SessionInfo si, Dictionary<string, string> customSubstVars)
        {
            int menuOption = -1;

            string menuOptionStr = customSubstVars.XFGetValue(Param_DashboardMenuOption, "1");


            if (!String.IsNullOrEmpty(menuOptionStr))
            {
                menuOption = Convert.ToInt32(menuOptionStr);
            }

            return menuOption;
        }

        public static DataTable getHeaderItems(SessionInfo si, Dictionary<string, string> customSubstVarsAlreadyResolved)
        {

            var wfUnitPk = BRApi.Workflow.General.GetWorkflowUnitPk(si);
            var ProfileKey = wfUnitPk.ProfileKey;
            int configProfileID = DDM_DB_Config_Support.getCurrentProfileID(si, ProfileKey);

            string menu_option = customSubstVarsAlreadyResolved.XFGetValue(Param_DashboardMenuOption, "1");

            var Menu_Hdr_Options_DT = new DataTable("Menu_Hdr_Options");

            var dbConnApp = BRApi.Database.CreateApplicationDbConnInfo(si);
            using (var connection = new SqlConnection(dbConnApp.ConnectionString))
            {
                var sql_gbl_get_datasets = new GBL_UI_Assembly.SQL_GBL_Get_DataSets(si, connection);
                // Create a new DataTable

                var sqlDataAdapter = new SqlDataAdapter();
                // Define the select query and parameters
                string selectQuery = @"
							        	Select *
										FROM DDM_Config_Menu_Hdr
										WHERE DDM_Profile_ID = @DDM_Profile_ID
										AND DDM_Menu_ID = @DDM_Menu_ID
										ORDER BY Order";

                // Create an array of SqlParameter objects
                var parameters = new SqlParameter[]
                {
                    new SqlParameter("@DDM_Profile_ID", SqlDbType.Int) { Value = configProfileID},
                    new SqlParameter("@DDM_Menu_ID", SqlDbType.Int) { Value = menu_option}
                };

                if (!String.IsNullOrEmpty(menu_option) && configProfileID != -1)
                {
                    sql_gbl_get_datasets.Fill_Get_GBL_DT(si, sqlDataAdapter, Menu_Hdr_Options_DT, selectQuery, parameters);
                }
            }

            return Menu_Hdr_Options_DT;
        }

    }
}

using System;
using System.Collections.Generic;
using System.Data;
using System.Data.Common;
using System.Globalization;
using System.IO;
using System.Linq;
using Microsoft.CSharp;
using Microsoft.Data.SqlClient;
using OneStream.Finance.Database;
using OneStream.Finance.Engine;
using OneStream.Shared.Common;
using OneStream.Shared.Database;
using OneStream.Shared.Engine;
using OneStream.Shared.Wcf;
using OneStream.Stage.Database;
using OneStream.Stage.Engine;

namespace Workspace.__WsNamespacePrefix.__WsAssemblyName.BusinessRule.DashboardDataSet.DDM_DB_DataSets
{
    /// <summary>
    /// MainClass provides data retrieval methods for Dynamic Dashboard Manager datasets.
    /// </summary>
    public class MainClass
    {
        #region "Global Variables"
        private SessionInfo si;
        private BRGlobals globals;
        private object api;
        private DashboardDataSetArgs args;
        #endregion

        /// <summary>
        /// Main entry point for the Dashboard DataSet business rule.
        /// Handles requests for dataset names and data retrieval based on function type and dataset name.
        /// </summary>
        public object Main(SessionInfo si, BRGlobals globals, object api, DashboardDataSetArgs args)
        {
            try
            {
                this.si = si;
                this.globals = globals;
                this.api = api;
                this.args = args;
                switch (args.FunctionType)
                {
                    case DashboardDataSetFunctionType.GetDataSetNames:
                        // Return available dataset names if needed
                        // var names = new List<string> { "MyDataSet" };
                        // return names;
                        break;
                    case DashboardDataSetFunctionType.GetDataSet:
                        // Return WF Root Profiles
                        if (args.DataSetName.XFEqualsIgnoreCase("Get_Root_WFProfiles"))
                        {
                            return Get_Root_WFProfiles();
                        }
                        // Return WF Profile Hierarchy for selected root profile
                        else if (args.DataSetName.XFEqualsIgnoreCase("Get_WFProfile_TreeView"))
                        {
                            return Get_WFProfile_TreeView();
                        }
                        // Return WF Profile Menu Options for selected profile
                        else if (args.DataSetName.XFEqualsIgnoreCase("Get_App_Menu"))
                        {
                            return Get_App_Menu();
                        }
                        // Return WF Profile Name/ID List for lookup in Table Editor
                        else if (args.DataSetName.XFEqualsIgnoreCase("Get_WFProfile_Names"))
                        {
                            return Get_WFProfile_Names();
                        }
                        // Return WF Profile Header Items for selected profile/menu option
                        else if (args.DataSetName.XFEqualsIgnoreCase("Get_WFProfile_Headers"))
                        {
                            return Get_WFProfile_Headers();
                        }
                        break;
                }
                return null;
            }
            catch (Exception ex)
            {
                // Log and rethrow exceptions for error handling
                throw ErrorHandler.LogWrite(si, new XFException(si, ex));
            }
        }

        #region "Get WF Profiles"
        /// <summary>
        /// Retrieves the root workflow profiles (HierarchyLevel = 1, not templates).
        /// </summary>
        private DataTable Get_Root_WFProfiles()
        {
            try
            {
                // Define the SQL Statement
                var sql = @"
                    SELECT ProfileName
                    FROM WorkflowProfileHierarchy
                    WHERE HierarchyLevel = 1
                        AND IsTemplate = 0 ";

                // Return the DataTable
                using (DbConnInfo dbConnApp = BRApi.Database.CreateApplicationDbConnInfo(si))
                {
                    DataTable dt = BRApi.Database.ExecuteSql(dbConnApp, sql, false);
                    dt.TableName = "Root_WFProfiles";
                    return dt;
                }
            }
            catch (Exception ex)
            {
                throw ErrorHandler.LogWrite(si, new XFException(si, ex));
            }
        }
		#endregion
		
		#region "Get WFProfile TreeView"

        /// <summary>
        /// Retrieves the workflow profile hierarchy as a tree view for the selected root profile.
        /// </summary>
        private DataSet Get_WFProfile_TreeView()
        {
            try
            {
                var hierarchy = new XFTreeItemCollection();
                var hierarchy_mbrs = new List<XFTreeItem>();
                var parent_child = new Dictionary<string, string>();
                string rootProfileName = args.CustomSubstVars.XFGetValue("BL_DDM_Root_WFProfiles");

                var dt = new DataTable();

                // Define the SQL Statement
                var sql = @"
                    WITH RecursiveCTE AS (
                        SELECT 
                            prof.ProfileKey,
                            prof.ProfileName, 
                            CAST('00000000-0000-0000-0000-000000000000' AS uniqueidentifier) AS ParentProfileKey, 
                            prof.HierarchyLevel, 
                            prof.HierarchyIndex
                        FROM 
                            WorkflowProfileHierarchy prof
                        WHERE 
                            prof.HierarchyLevel = 1
                            AND prof.IsTemplate = 0
                            AND prof.ProfileName = @rootprofilename 
                        UNION ALL
                        SELECT 
                            prof.ProfileKey,
                            prof.ProfileName,
                            prof.ParentProfileKey, 
                            prof.HierarchyLevel, 
                            prof.HierarchyIndex
                        FROM 
                            WorkflowProfileHierarchy prof
                        INNER JOIN 
                            RecursiveCTE rcte ON prof.ParentProfileKey = rcte.ProfileKey
                    )
                    SELECT 
                        rcte.ProfileName, 
                        rcte.ProfileKey,
                        rcte.ParentProfileKey, 
                        parentProf.ProfileName as ParentProfileName, 
                        rcte.HierarchyLevel,
                        rcte.HierarchyIndex,
                        DDM.DDM_Profile_ID
                    FROM 
                        RecursiveCTE rcte
                    LEFT JOIN 
                        WorkflowProfileHierarchy parentProf ON rcte.ParentProfileKey = parentProf.ProfileKey
                    LEFT JOIN
                        DDM_Config DDM ON DDM.DDM_Profile_Name = rcte.ProfileKey
                    ORDER BY 
                        rcte.HierarchyLevel DESC, 
                        rcte.HierarchyIndex";

                // Return the DataTable
                var dbConnApp = BRApi.Database.CreateApplicationDbConnInfo(si);
                using (var connection = new SqlConnection(dbConnApp.ConnectionString))
                {
                    var sql_GBL_Get_DataSets = new GBL_UI_Assembly.SQL_GBL_Get_DataSets(si, connection);
                    var sqa = new SqlDataAdapter();

                    var sqlparams = new SqlParameter[]
                    {
                        new SqlParameter("@rootprofilename", SqlDbType.NVarChar,100) { Value = rootProfileName }
                    };

                    sql_GBL_Get_DataSets.Fill_Get_GBL_DT(si, sqa, dt, sql, sqlparams);
                }

                // Build the tree structure from the DataTable
                foreach (DataRow row in dt.Rows)
                {
                    var hierarchy_children_mbrs = new List<XFTreeItem>();
                    string profileName = row["ProfileName"].ToString();
                    string profileKey = row["ProfileKey"].ToString();
                    string parentprofileName = row["ParentProfileName"].ToString();
                    string parentprofileKey = row["ParentProfileKey"].ToString();
                    var Bold_WFProfile = true;
                    if (row["DDM_Profile_ID"] == DBNull.Value)
                    {
                        Bold_WFProfile = false;
                    }
                    parent_child.Add(profileKey, parentprofileKey);
                    var childProfiles = parent_child.Where(pair => pair.Value == profileKey)
                                        .Select(pair => pair.Key)
                                        .ToList();
                    if (childProfiles.Count > 0)
                    {
                        foreach (var childProfile in childProfiles)
                        {
                            // Create an XFTreeItem for each child profile
                            var childXFTreeItem = hierarchy_mbrs.Find(item => item.UniqueName == childProfile);
                            hierarchy_children_mbrs.Add(childXFTreeItem);
                        }
                        var wfprofile_xftreeitem = new XFTreeItem(profileKey, profileName, string.Empty, Bold_WFProfile, true, true, true, XFImageFileSourceType.Unknown, string.Empty, string.Empty, hierarchy_children_mbrs, TriStateBool.TrueValue);
                        hierarchy_mbrs.Add(wfprofile_xftreeitem);
                    }
                    else
                    {
                        var wfprofile_xftreeitem = new XFTreeItem(profileKey, profileName, string.Empty, Bold_WFProfile, true, true, true, XFImageFileSourceType.Unknown, string.Empty, string.Empty, hierarchy_children_mbrs, TriStateBool.TrueValue);
                        hierarchy_mbrs.Add(wfprofile_xftreeitem);
                    }
                }

                var hierXFTreeItem = hierarchy_mbrs.Find(item => item.HeaderText == rootProfileName);
                hierarchy.TreeItems.Add(hierXFTreeItem);
                return hierarchy.CreateDataSet(si);
            }
            catch (Exception ex)
            {
                throw ErrorHandler.LogWrite(si, new XFException(si, ex));
            }
        }
		#endregion

		#region "Get WFProfile Menus"
        /// <summary>
        /// Retrieves the menu options for a given workflow profile.
        /// </summary>
        private DataTable Get_App_Menu()
        {
            try
            {
                var wfUserPk = BRApi.Workflow.General.GetWorkflowUnitPk(si);
                var dt = new DataTable("DDM_Config_Menu");
                // Define the SQL Statement
                var sql = @"
                    Select Menu.DDM_Menu_ID, Menu.Name
                    FROM DDM_Config Cnfg
                    JOIN DDM_Config_Menu Menu
                    ON Cnfg.DDM_Profile_ID = Menu.DDM_Profile_ID
                    WHERE Cnfg.DDM_Profile_Name = @ProfileKey
                    ORDER BY DDM_Menu_Order";

                // Return the DataTable
                var dbConnApp = BRApi.Database.CreateApplicationDbConnInfo(si);
                using (var connection = new SqlConnection(dbConnApp.ConnectionString))
                {
					connection.Open();
                    var sql_GBL_Get_DataSets = new GBL_UI_Assembly.SQL_GBL_Get_DataSets(si, connection);
                    var sqa = new SqlDataAdapter();

                    // Add the parameter for ProfileKey
                    var sqlparams = new SqlParameter[]
                    {
                        new SqlParameter("@ProfileKey", SqlDbType.UniqueIdentifier) { Value = wfUserPk.ProfileKey }
                    };

                    sql_GBL_Get_DataSets.Fill_Get_GBL_DT(si, sqa, dt, sql, sqlparams);
                }
				BRApi.ErrorLog.LogMessage(si,$"Hit {sql} - {wfUserPk.ProfileKey.ToString()} - {dt.Rows.Count.ToString()}");
                return dt;
            }
            catch (Exception ex)
            {
                throw ErrorHandler.LogWrite(si, new XFException(si, ex));
            }
        }
		#endregion

        /// <summary>
        /// Retrieves a list of workflow profile names and IDs (not templates).
        /// </summary>
        private DataTable Get_WFProfile_Names()
        {
            try
            {
                // Define the SQL Statement
                var sql = @"
                    SELECT ProfileName, ProfileKey
                    FROM WorkflowProfileHierarchy
                    WHERE IsTemplate = 0 ";

                // Return the DataTable
                using (DbConnInfo dbConnApp = BRApi.Database.CreateApplicationDbConnInfo(si))
                {
                    DataTable dt = BRApi.Database.ExecuteSql(dbConnApp, sql, false);
                    dt.TableName = "WFProfile_Names";
                    return dt;
                }
            }
            catch (Exception ex)
            {
                throw ErrorHandler.LogWrite(si, new XFException(si, ex));
            }
        }

		#region "Get WF Profile Headers"
        /// <summary>
        /// Retrieves header configuration options for a given workflow profile and menu option.
        /// </summary>
        private DataTable Get_WFProfile_Headers()
        {
            try
            {
                var wfUserPk = BRApi.Workflow.General.GetWorkflowUnitPk(si);
                var WFProfile_Header_Config_DT = new DataTable("WFProfile_Headers");
                string optionID = null;

                // Define the SQL Statement
                var sql = @"
                    SELECT *
                    FROM DDM_Config_Menu_Hdr
                    WHERE DDM_Profile_ID = @ProfileKey
                    AND DDM_Menu_ID = @OptionID";

                // Return the DataTable
                var dbConnApp = BRApi.Database.CreateApplicationDbConnInfo(si);
                using (var connection = new SqlConnection(dbConnApp.ConnectionString))
                {
                    var sql_GBL_Get_DataSets = new GBL_UI_Assembly.SQL_GBL_Get_DataSets(si, connection);
                    var sqa = new SqlDataAdapter();

                    // Add the parameter for ProfileKey and selected menu option 
                    var sqlparams = new SqlParameter[]
                    {
                        new SqlParameter("@ProfileKey", SqlDbType.UniqueIdentifier) { Value = wfUserPk.ProfileKey },
                        new SqlParameter("@OptionID", SqlDbType.UniqueIdentifier) { Value = optionID }
                    };

                    sql_GBL_Get_DataSets.Fill_Get_GBL_DT(si, sqa, WFProfile_Header_Config_DT, sql, sqlparams);
                }
                return WFProfile_Header_Config_DT;
            }
            catch (Exception ex)
            {
                throw ErrorHandler.LogWrite(si, new XFException(si, ex));
            }
        }
        #endregion
    }
}

